import React, { useState } from 'react';
import styled from 'styled-components';
import BuzzwordCell from './BuzzwordCell';

type BuzzwordBoardProps = {
    buzzwords: string[];
    markedCells: boolean[];
    handleCellClick: (index: number) => void;
    numcols: number;
    numrows: number;
};

const BuzzwordBoardContainer = styled.div`
  display: flex;
  justify-content: center;
`;

const BoardGrid = styled.div<{ numcols: number }>`
  display: grid;
  grid-template-columns: repeat(${(props) => props.numcols}, 1fr);
  max-width: 500px;
  margin: 0 auto;
  text-align: center;
`;

const Overlay = styled.div<{ show: boolean }>`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 999;
  animation: fadeIn 2s ease-in-out forwards;

  @keyframes fadeIn {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }

  ${(props) =>
        !props.show &&
        `
    display: none;
  `}
`;

const CongratsMessage = styled.h2`
  color: #fff;
  font-size: 24px;
  text-align: center;
`;

const BuzzwordBoard: React.FC<BuzzwordBoardProps> = ({ numcols, numrows, buzzwords, markedCells, handleCellClick }) => {
    const [isBingo, setIsBingo] = useState(false);
    const isCenterCell = (row: number, col: number): boolean => row === Math.floor(numrows / 2) && col === Math.floor(numcols / 2);

    const isLeftDiagonalCell = (row: number, col: number): boolean => row === col;

    const isRightDiagonalCell = (row: number, col: number): boolean => row + col === numcols - 1;

    const isRowHighlighted = (row: number): boolean => Array.from({ length: numcols }, (_, col) => markedCells[row * numcols + col]).every(Boolean);

    const isColHighlighted = (col: number): boolean => Array.from({ length: numrows }, (_, row) => markedCells[row * numcols + col]).every(Boolean);

    const isDiagonalHighlighted = (row: number, col: number): boolean => {
        if (isCenterCell(row, col)) return false;

        if (isLeftDiagonalCell(row, col)) return Array.from({ length: numrows }, (_, i) => markedCells[i * numcols + i]).every(Boolean);

        if (isRightDiagonalCell(row, col)) return Array.from({ length: numrows }, (_, i) => markedCells[i * numcols + (numcols - 1 - i)]).every(Boolean);

        return false;
    };

    return (
        <BuzzwordBoardContainer>
            <BoardGrid numcols={numcols}>
                {buzzwords.map((buzzword, index) => {
                    const row = Math.floor(index / numcols);
                    const col = index % numcols;
                    const ishighlighted = isRowHighlighted(row) || isColHighlighted(col) || isDiagonalHighlighted(row, col);

                    return (
                        <BuzzwordCell
                            key={index}
                            value={buzzword}
                            ismarked={markedCells[index]}
                            ishighlighted={ishighlighted}
                            onClick={() => handleCellClick(index)}
                            setIsBingo={setIsBingo}
                        />
                    );
                })}
            </BoardGrid>
            <Overlay show={isBingo}><CongratsMessage>Bingoo!!!!</CongratsMessage></Overlay>
        </BuzzwordBoardContainer>
    );
};

export default BuzzwordBoard;
