import React, { useState } from 'react';
import BuzzwordBoard from './BuzzwordBoard';
import styled from 'styled-components';
import Button from './Button';

interface BuzzwordBingoProps {
    buzzwords: Array<string>,
    numrows: number,
    numcols: number,
}

const SecondHalf = styled.div`
    width: 55%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const Overlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 999;
`;

const CongratsMessage = styled.h2`
  color: #fff;
  font-size: 24px;
  text-align: center;
`;

const BuzzwordBingo: React.FC<BuzzwordBingoProps> = ({
    buzzwords,
    numrows,
    numcols,
}) => {
    const [markedCells, setMarkedCells] = useState<boolean[]>(new Array(25).fill(false));
    const [isBingo, setIsBingo] = useState(false);
    const handleCellClick = (index: number) => {
        if (markedCells[index]) return;

        // Mark the cell as selected
        setMarkedCells(prev => [...prev.slice(0, index), true, ...prev.slice(index + 1)]);
    };

    const handleReset = () => setMarkedCells(Array(numcols * numrows).fill(false));

    return (
        <SecondHalf>
            <BuzzwordBoard
                buzzwords={buzzwords}
                markedCells={markedCells}
                handleCellClick={handleCellClick}
                numcols={numcols}
                numrows={numrows}
            />
        </SecondHalf>
    );
};


export default BuzzwordBingo;
