import React, { useMemo } from 'react';
import BuzzwordBingo from './pages/BuzzwordBingo';
import styled, { css } from 'styled-components'

import { buzzwordsData } from './data';

const numcols = 5;
const numrows = 5;

const MainContainer = styled.div`
    display: flex;
    width: 100vw;
    height: 100vh;
    flex-direction: column;
    align-items: center;
    background: linear-gradient(to bottom, var(--color-primary), var(--color-background));
`;

const Header = styled.h1`
  font-size: var(--font-size-mega);
  text-align: center;
`;

const App: React.FC = () => {
  // Memoizing to reduce re-rendering
  const data = useMemo(() => {
    const copyOfBuzzwordsData = [...buzzwordsData];
    const middleIndex = Math.floor(copyOfBuzzwordsData.length / 2);
    copyOfBuzzwordsData.splice(middleIndex, 0, 'free');

    return copyOfBuzzwordsData;
  }, []);

  return (
    <div className="App">
      <MainContainer>
        {/* <Header>
          Buzzword
          <br />
          Bingo
        </Header> */}
        <BuzzwordBingo buzzwords={data} numcols={numcols} numrows={numrows} />
      </MainContainer>
    </div>
  );
};

export default App;
