import React from 'react';
import styled, { keyframes, css } from 'styled-components';

interface BuzzwordCellProps {
    value: string;
    ismarked: boolean;
    ishighlighted: boolean;
    onClick?: () => void;
    style?: React.CSSProperties; // Add the style property to the interface
}

const glowAnimation = keyframes`
  0% {
    box-shadow: 0 0 0 0 #23e9a5;
  }
  50% {
    box-shadow: 0 0 10px 5px #23e9a5;
  }
  100% {
    box-shadow: 0 0 0 0 #23e9a5;
  }
`;

const CellContainer = styled.div<{ ismarked: boolean; ishighlighted: boolean }>`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: ${(props) => (props.ismarked ? '#9fd8cb' : '#f2f2f2')};
  color: ${(props) => (props.ismarked ? '#ffffff' : '#000000')};
  font-weight: bold;
  font-size: 16px;
  width: 130px;
  height: 130px;
  cursor: pointer;
  border: 1px solid #000;

  ${(props) =>
        props.ishighlighted &&
        css`
      background-color: #000000;
    `}

    ${({ ismarked, ishighlighted }) =>
        ismarked &&
        ishighlighted &&
        css`
      animation: ${glowAnimation} 1s infinite;
    `}
`;

const BuzzwordCell: React.FC<BuzzwordCellProps> = ({
    value,
    ismarked,
    ishighlighted,
    onClick,
}) => {
    return (
        <CellContainer
            ismarked={ismarked}
            ishighlighted={ishighlighted}
            onClick={onClick}
        >
            {value}
        </CellContainer>
    );
};

export default BuzzwordCell;
