import React, { useEffect } from 'react';
import styled, { css } from 'styled-components';
import bingoSound from './bingo.mp3';

interface BuzzwordCellProps {
    value: string;
    ismarked: boolean;
    ishighlighted: boolean;
    onClick?: () => void;
    style?: React.CSSProperties;
    ref?: HTMLInputElement;
    setIsBingo: any;
}

const CellContainer = styled.div<{ ismarked: boolean; ishighlighted: boolean }>`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: ${(props) => (props.ismarked ? 'rgba(254, 91, 124, 1)' : '#f2f2f2')};
  color: ${(props) => (props.ismarked ? '#ffffff' : '#000000')};
  font-weight: bold;
  font-size: 16px;
  width: 130px;
  height: 130px;
  cursor: pointer;
  border: 1px solid #000;

  ${(props) =>
        props.ishighlighted &&
        css`
      background-color: linear-gradient(49deg, rgba(114, 255, 249, 1) 9%, rgba(248, 255, 121, 1) 45%);;
    `}
`;

const BuzzwordCell: React.FC<BuzzwordCellProps> = ({
    value,
    ismarked,
    ishighlighted,
    onClick,
    setIsBingo,
}) => {
    useEffect(() => {
        if(ismarked && ishighlighted) {
            const audio = new Audio(bingoSound);
            audio.play();
            setIsBingo(true);
        }
    }, [ismarked, ishighlighted])

    return (
        <CellContainer
            ismarked={ismarked}
            ishighlighted={ishighlighted}
            onClick={onClick}
        >
            {value}
        </CellContainer>
    );
};

export default BuzzwordCell;
