import React, { useState } from 'react';
import BuzzwordBoard from './BuzzwordBoard';

interface BuzzwordBingoProps {
    buzzwords: Array<string>,
    numrows: number,
    numcols: number,
}

const BuzzwordBingo: React.FC<BuzzwordBingoProps> = ({
    buzzwords,
    numrows,
    numcols,
}) => {
    const [markedCells, setMarkedCells] = useState<boolean[]>(new Array(numrows * numcols).fill(false));
    const handleCellClick = (index: number) => {
        setMarkedCells(prev => [...prev.slice(0, index), true, ...prev.slice(index + 1)]);
    };

    // Insert free inbetween based on size dynamically
    buzzwords.splice(Math.floor(buzzwords.length / 2), 0, 'free');

    return (
        <div>
            <h1>Buzzword Bingo</h1>
            <BuzzwordBoard
                buzzwords={buzzwords}
                markedCells={markedCells}
                handleCellClick={handleCellClick}
                numcols={numcols}
                numrows={numrows}
            />
        </div>
    );
};


export default BuzzwordBingo;
