import React from 'react';
import styled, { css } from 'styled-components';

interface BuzzwordCellProps {
    value: string;
    isMarked: boolean;
    isHighlighted: boolean;
    onClick?: () => void;
    style?: React.CSSProperties; // Add the style property to the interface
  }

const CellContainer = styled.div<{ isMarked: boolean; isHighlighted: boolean }>`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: ${(props) => (props.isMarked ? '#9fd8cb' : '#f2f2f2')};
  color: ${(props) => (props.isMarked ? '#ffffff' : '#000000')};
  font-weight: bold;
  font-size: 16px;
  width: 100px;
  height: 100px;
  cursor: pointer;

  ${(props) =>
        props.isHighlighted &&
        css`
      background-color: #ffd700;
    `}
`;

const BuzzwordCell: React.FC<BuzzwordCellProps> = ({
    value,
    isMarked,
    isHighlighted,
    onClick,
}) => {
    return (
        <CellContainer
            isMarked={isMarked}
            isHighlighted={isHighlighted}
            onClick={onClick}
        >
            {value}
        </CellContainer>
    );
};

export default BuzzwordCell;
