import React from 'react';
import BuzzwordCell from './BuzzwordCell';

type BuzzwordBoardProps = {
    buzzwords: string[];
    markedCells: boolean[];
    onCellClick: (index: number) => void;
};

const BuzzwordBoard: React.FC<BuzzwordBoardProps> = ({
    buzzwords,
    markedCells,
    onCellClick,
}) => {
    return (
        <div className="buzzword-board">
            {buzzwords.map((buzzword, index) => (
                <BuzzwordCell
                    key={index}
                    value={buzzword}
                    isMarked={markedCells[index]}
                    onClick={() => onCellClick(index)}
                />
            ))}
        </div>
    );
};

export default BuzzwordBoard;
