import React, { useEffect } from 'react';
import BuzzwordBingo from './BuzzwordBingo';
import styled from 'styled-components';
import introMusic from './intro.mp3'

import { buzzwordsData } from './data';

const numcols = 5;
const numrows = 5;

buzzwordsData.splice(Math.floor(buzzwordsData.length / 2), 0, 'free');

const MainContainer = styled.div`
  display: flex;
  width: 100vw;
  height: 100vh;
`;

const FirstHalf = styled.div`
  display: flex;
  width: 30%;
  background: linear-gradient(to bottom, #23e9a5, #fff);
  flex-direction: row;
  align-items: center;
  justify-content: center;
`;

const Header = styled.h1`
  font-family: 'Satisfy', cursive;
  font-size: 60px;
`;

const App: React.FC = () => {

  useEffect(() => {
    const audio = new Audio(introMusic);
    audio.play();
  })

  return (
    <div className="App">
      <MainContainer>
        <FirstHalf>
          <Header>
            Buzzword
            <br />
            Bingo
          </Header>
        </FirstHalf>
        <BuzzwordBingo buzzwords={buzzwordsData} numcols={numcols} numrows={numrows} />
      </MainContainer>
    </div>
  );
};

export default App;
