import React, { useState, useMemo } from 'react';
import BuzzwordBoard from './BuzzwordBoard';

interface BuzzwordBingoProps {
    buzzwords: Array<string>,
    numrows: number,
    numcols: number,
}

const BuzzwordBingo: React.FC<BuzzwordBingoProps> = ({
    buzzwords,
    numrows,
    numcols,
}) => {
    const [markedCells, setMarkedCells] = useState<boolean[]>(new Array(25).fill(false));
    const handleCellClick = (index: number) => {
        if (markedCells[index]) {
          // Cell is already marked, do nothing
          return;
        }

        // Mark the cell as selected
        setMarkedCells(prev => [...prev.slice(0, index), true, ...prev.slice(index + 1)]);
      };


    return (
        <div>
            <BuzzwordBoard
                buzzwords={buzzwords}
                markedCells={markedCells}
                handleCellClick={handleCellClick}
                numcols={numcols}
                numrows={numrows}
            />
        </div>
    );
};


export default BuzzwordBingo;
