import React from 'react';
import styled, { css } from 'styled-components';
import BuzzwordCell from './BuzzwordCell';

type BuzzwordBoardProps = {
  buzzwords: string[];
  markedCells: boolean[];
  onCellClick: (index: number) => void;
};

const BuzzwordBoardContainer = styled.div`
  display: flex;
  justify-content: center;
`;

const BoardGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  grid-gap: 10px;
  max-width: 500px;
  margin: 0 auto;
`;

const BuzzwordBoard: React.FC<BuzzwordBoardProps> = ({
  buzzwords,
  markedCells,
  onCellClick,
}) => {
  const numRows = 5;
  const numCols = 5;

  const isRowHighlighted = (rowIndex: number) => {
    for (let col = 0; col < numCols; col++) {
      const index = rowIndex * numCols + col;
      if (!markedCells[index]) {
        return false;
      }
    }
    return true;
  };

  const isColHighlighted = (colIndex: number) => {
    for (let row = 0; row < numRows; row++) {
      const index = row * numCols + colIndex;
      if (!markedCells[index]) {
        return false;
      }
    }
    return true;
  };

  return (
    <BuzzwordBoardContainer>
      <BoardGrid>
        {buzzwords.map((buzzword, index) => {
          const row = Math.floor(index / numCols);
          const col = index % numCols;
          const isHighlighted = isRowHighlighted(row) || isColHighlighted(col);

          return (
            <BuzzwordCell
              key={index}
              value={buzzword}
              isMarked={markedCells[index]}
              isHighlighted={isHighlighted}
              onClick={() => onCellClick(index)}
            />
          );
        })}
      </BoardGrid>
    </BuzzwordBoardContainer>
  );
};

export default BuzzwordBoard;
