import React, { useState } from 'react';
import BuzzwordBoard from './BuzzwordBoard';
import styled from 'styled-components';
import Button from './Button';

interface BuzzwordBingoProps {
    buzzwords: Array<string>,
    numrows: number,
    numcols: number,
}

const SecondHalf = styled.div`
    width: 55%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const BuzzwordBingo: React.FC<BuzzwordBingoProps> = ({
    buzzwords,
    numrows,
    numcols,
}) => {
    const [markedCells, setMarkedCells] = useState<boolean[]>(new Array(25).fill(false));
    const handleCellClick = (index: number) => {
        if (index === Math.floor((numrows * numcols) / 2)) {
          // Center cell ("Free") clicked
          return;
        }

        const newMarkedCells = [...markedCells];
        newMarkedCells[index] = !newMarkedCells[index];
        newMarkedCells[Math.floor((numrows * numcols) / 2)] = true; // Select the center cell
        setMarkedCells(newMarkedCells);
      };

    const handleReset = () => setMarkedCells(Array(numcols * numrows).fill(false));

    return (
        <SecondHalf>
            <BuzzwordBoard
                buzzwords={buzzwords}
                markedCells={markedCells}
                handleCellClick={handleCellClick}
                numcols={numcols}
                numrows={numrows}
            />
            <Button onClick={handleReset}>Reset</Button>
        </SecondHalf>
    );
};


export default BuzzwordBingo;
