import React, { useState } from 'react';
import BuzzwordBoard from './BuzzwordBoard';

const BuzzwordBingo: React.FC = () => {
    const [markedCells, setMarkedCells] = useState<boolean[]>(Array(25).fill(false));

    const handleCellClick = (index: number) => {
        const updatedMarkedCells = [...markedCells];
        updatedMarkedCells[index] = !updatedMarkedCells[index];
        setMarkedCells(updatedMarkedCells);
    };

    const buzzwords = [
        "I'll be back.", // Terminator (1984)
        "May the Force be with you.", // Star Wars (1977)
        "You can't handle the truth!", // A Few Good Men (1992)
        "Here's looking at you, kid.", // Casablanca (1942)
        "I'm king of the world!", // Titanic (1997)
        "To infinity and beyond!", // Toy Story (1995)
        "I'll never let go, Jack.", // Titanic (1997)
        "Why so serious?", // The Dark Knight (2008)
        "I'm just a girl, standing in front of a boy, asking him to love her.", // Notting Hill (1999)
        "Houston, we have a problem.", // Apollo 13 (1995)
        "Frankly, my dear, I don't give a damn.", // Gone with the Wind (1939)
        "Keep your friends close, but your enemies closer.", // The Godfather Part II (1974)
        "Hasta la vista, baby.", // Terminator 2: Judgment Day (1991)
        "There's no place like home.", // The Wizard of Oz (1939)
        "I am your father.", // Star Wars: Episode V - The Empire Strikes Back (1980)
        "Life is like a box of chocolates; you never know what you're gonna get.", // Forrest Gump (1994)
        "I see dead people.", // The Sixth Sense (1999)
        "I feel the need...the need for speed!", // Top Gun (1986)
        "I'll have what she's having.", // When Harry Met Sally... (1989)
        "You talking to me?", // Taxi Driver (1976)
        "I'm too old for this shit.", // Lethal Weapon (1987)
        "Bond. James Bond.", // Dr. No (1962)
        "Say hello to my little friend!", // Scarface (1983)
        "Gentlemen, you can't fight in here! This is the War Room!", // Dr. Strangelove (1964),
        "I am all your got"
    ];

    return (
        <div className="buzzword-bingo">
            <h1>Buzzword Bingo</h1>
            <BuzzwordBoard
                buzzwords={buzzwords}
                markedCells={markedCells}
                onCellClick={handleCellClick}
            />
        </div>
    );
};

export default BuzzwordBingo;
