import React from 'react';
import BuzzwordBingo from './BuzzwordBingo';
import styled from 'styled-components';

import { buzzwordsData } from './data';

const numcols = 5;
const numrows = 5;

buzzwordsData.splice(Math.floor(buzzwordsData.length / 2), 0, 'free');

const Main = styled.div`
  height: 100vh;
  width: 100vw'
`

const MainContainer = styled.div`
  display: flex;
`;

const FirstHalf = styled.div`
  width: 30%;
  background: linear-gradient(to bottom, #23e9a5, #fff);
`;

const Header = styled.h1`
  font-family: 'Satisfy', cursive;
  font-size: 50px;
`;

const App: React.FC = () => {
  return (
    <Main>
      <div className="App">
        <MainContainer>
          <FirstHalf>
            <Header>
              Buzzword
              <br />
              Bingo
            </Header>
          </FirstHalf>
          <BuzzwordBingo buzzwords={buzzwordsData} numcols={numcols} numrows={numrows} />
        </MainContainer>
      </div>
    </Main>
  );
};

export default App;
