import React from 'react';
import styled, { css } from 'styled-components';
import BuzzwordCell from './BuzzwordCell';

type BuzzwordBoardProps = {
  buzzwords: string[];
  markedCells: boolean[];
  onCellClick: (index: number) => void;
};

const BuzzwordBoardContainer = styled.div`
  display: flex;
  justify-content: center;
`;

const BoardGrid = styled.div<{ numCols: number }>`
  display: grid;
  grid-template-columns: repeat(${(props) => props.numCols}, 1fr);
  grid-gap: 10px;
  max-width: 500px;
  margin: 0 auto;
`;

const BuzzwordBoard: React.FC<BuzzwordBoardProps> = ({
  buzzwords,
  markedCells,
  onCellClick,
}) => {
  const numCols = 5; // Modify this value to change the number of columns
  const numRows = Math.ceil(buzzwords.length / numCols);

  const isRowHighlighted = (rowIndex: number) => {
    for (let col = 0; col < numCols; col++) {
      const index = rowIndex * numCols + col;
      if (!markedCells[index]) {
        return false;
      }
    }
    return true;
  };

  const isColHighlighted = (colIndex: number) => {
    for (let row = 0; row < numRows; row++) {
      const index = row * numCols + colIndex;
      if (!markedCells[index]) {
        return false;
      }
    }
    return true;
  };

  const isDiagonalHighlighted = () => {
    const leftDiagonal = [];
    const rightDiagonal = [];

    for (let i = 0; i < numRows; i++) {
      leftDiagonal.push(i * (numCols + 1));
      rightDiagonal.push((i + 1) * (numCols - 1));
    }

    const isLeftDiagonalMarked = leftDiagonal.every(
      (index) => markedCells[index]
    );
    const isRightDiagonalMarked = rightDiagonal.every(
      (index) => markedCells[index]
    );

    return isLeftDiagonalMarked || isRightDiagonalMarked;
  };

  const isBingo = () => {
    // Check rows
    for (let row = 0; row < numRows; row++) {
      if (isRowHighlighted(row)) {
        return true;
      }
    }

    // Check columns
    for (let col = 0; col < numCols; col++) {
      if (isColHighlighted(col)) {
        return true;
      }
    }

    // Check diagonals
    if (isDiagonalHighlighted()) {
      return true;
    }

    return false;
  };

  return (
    <BuzzwordBoardContainer>
      <BoardGrid numCols={numCols}>
        {buzzwords.map((buzzword, index) => {
          const row = Math.floor(index / numCols);
          const col = index % numCols;
          const isHighlighted = isRowHighlighted(row) || isColHighlighted(col);

          return (
            <BuzzwordCell
              key={index}
              value={buzzword}
              isMarked={markedCells[index]}
              isHighlighted={isHighlighted}
              onClick={() => onCellClick(index)}
            />
          );
        })}
      </BoardGrid>
    </BuzzwordBoardContainer>
  );
};

export default BuzzwordBoard;
