import styled from 'styled-components';

const Button = styled.button`
    padding: 12px 24px;
    font-size: 16px;
    font-weight: 500;
    border: none;
    border-radius: 24px;
    background-color: #23e9a5;
    color: #ffffff;
    cursor: pointer;
    transition: background-color 0.3s ease;
    width: 158px;
    font-family: 'Satisfy', cursive;
    font-size: 22px;

    &:hover {
        background-color: #1bc99b;
    }

    &:focus {
        outline: none;
    }

    &:disabled {
        background-color: #cccccc;
        cursor: not-allowed;
    }
`;

export default Button;
